import React, { useState, useEffect } from 'react';
import { Card, Form, Button, Alert, Nav } from 'react-bootstrap';
import ChallengeInterface from '../components/ChallengeInterface';
import challengeData from '../challengeData';

const WebExploitationChallenge1 = () => {
  const [currentPage, setCurrentPage] = useState('login');
  const [theme, setTheme] = useState('light');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [uploadedFile, setUploadedFile] = useState(null);
  const [showFlag, setShowFlag] = useState(false);

  const challenge = challengeData.webExploitation.find(c => c.id === 'web1');

  useEffect(() => {
    if (currentPage === 'login' && theme === 'dark') {
      console.log("Current URL: nigol.php");
    }
  }, [currentPage, theme]);

  const checkCredentials = (username, password) => {
    // Vulnerable query simulation
    let query = `SELECT * FROM users WHERE username = '${username}' AND password = '${password}' ORDER BY `;

    // Check if the ORDER BY clause is being manipulated
    if (password.toLowerCase().includes('order by')) {
      query += password;
    } else {
      query += 'username';
    }

    console.log("Executed query:", query);

    // Simple check for successful injection
    return password.toLowerCase().includes('order by') && password.includes('is_admin DESC');
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (checkCredentials(username, password)) {
      setIsLoggedIn(true);
      setCurrentPage('upload');
    } else {
      alert('Invalid credentials');
    }
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file && (file.name.endsWith('.php2') || file.name.endsWith('.php3'))) {
      setUploadedFile(file);
    } else {
      alert('Invalid file type. Please upload a .php2 or .php3 file.');
    }
  };

  const handleFileSubmission = () => {
    if (uploadedFile) {
      setShowFlag(true);
    }
  };

  const LoginPage = () => (
    <div>
      <Nav variant="tabs" className="mb-3">
        <Nav.Item>
          <Nav.Link onClick={() => setTheme('light')}>Light Theme</Nav.Link>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link onClick={() => setTheme('dark')}>Dark Theme (nigol.php)</Nav.Link>
        </Nav.Item>
      </Nav>
      <Form onSubmit={handleLogin}>
        <Form.Group className="mb-3">
          <Form.Label>Username:</Form.Label>
          <Form.Control 
            type="text" 
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter username"
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Password:</Form.Label>
          <Form.Control 
            type="password" 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter password"
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Login
        </Button>
      </Form>
    </div>
  );

  const UploadPage = () => (
    <div>
      <h4>File Upload</h4>
      <Form.Group className="mb-3">
        <Form.Label>Upload PHP File (.php2 or .php3 only):</Form.Label>
        <Form.Control 
          type="file" 
          onChange={handleFileUpload}
          accept=".php2,.php3"
        />
      </Form.Group>
      <Button variant="primary" onClick={handleFileSubmission} disabled={!uploadedFile}>
        Submit File
      </Button>
      {showFlag && (
        <Alert variant="success" className="mt-3">
          Congratulations! You've successfully exploited both vulnerabilities. The flag is: {challenge.flag}
        </Alert>
      )}
    </div>
  );

  const challengeContent = (
    <Card className="mt-4">
      <Card.Body>
        <Card.Title>{challenge.title}</Card.Title>
        <Card.Text>{challenge.description}</Card.Text>
        <Alert variant="info">
          <strong>Hint:</strong> {challenge.hint}
        </Alert>

        {isLoggedIn ? <UploadPage /> : <LoginPage />}

      </Card.Body>
    </Card>
  );

  return (
    <ChallengeInterface challenge={challenge}>
      {challengeContent}
    </ChallengeInterface>
  );
};

export default WebExploitationChallenge1;
