import React, { useState, useEffect } from 'react';
import { Card } from 'react-bootstrap';
import { calculateTotalScore } from '../utils/scoreManager';

const ScoreCard = () => {
  const [score, setScore] = useState(0);

  useEffect(() => {
    const updateScore = () => {
      const newScore = calculateTotalScore();
      setScore(newScore);
    };

    // Initial score calculation
    updateScore();

    // Listen for changes in localStorage
    window.addEventListener('storage', updateScore);

    // Clean up the event listener
    return () => {
      window.removeEventListener('storage', updateScore);
    };
  }, []);

  return (
    <Card className="mb-3">
      <Card.Body>
        <Card.Title>Agent Score</Card.Title>
        <Card.Text>
          Total Points: <strong>{score}</strong>
        </Card.Text>
      </Card.Body>
    </Card>
  );
};

export default ScoreCard;
