import React from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap';
import ProgressBar from 'react-bootstrap/ProgressBar';
import { Link } from 'react-router-dom';

const categories = [
  { name: "Web Exploitation", path: "/web-exploitation" },
  { name: "Cryptography", path: "/cryptography" },
  { name: "Network Security", path: "/network-security" },
  { name: "Reverse Engineering",  path: "/reverse-engineering" },
  { name: "Binary Exploitation",  path: "/binary-exploitation" },
];

const ChallengeLanding = () => {
  return (
    <Container className="py-5 position-relative" fluid style={{ minHeight: "100vh" }}>

      <Row className="justify-content-center">
        <Col xs={12} md={8} lg={6}>
          {categories.map((category, index) => (
            <div key={index} className="mb-4">
              <h5 className="mb-2">
                <Link 
                  to={category.path} 
                  className="text-decoration-none text-dark category-link"
                  style={{
                    transition: 'color 0.3s ease, transform 0.3s ease',
                    display: 'inline-block'
                  }}
                >
                  {category.name}
                </Link>
              </h5>
            </div>
          ))}
        </Col>
      </Row>
      <style>
        {`
          .category-link:hover {
            color: #007bff !important;
            transform: translateX(5px);
          }
        `}
      </style>
    </Container>
  );
};

export default ChallengeLanding;
