import React, { useState, useCallback, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Card, Form, Button, Alert, Nav } from 'react-bootstrap';
import ChallengeInterface from '../components/ChallengeInterface';
import challengeData from '../challengeData';

const WebExploitationChallenge1 = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [uploadedFile, setUploadedFile] = useState(null);
  const [showFlag, setShowFlag] = useState(false);

  const challenge = challengeData.webExploitation.find(c => c.id === 'web1');

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    if (!searchParams.get('page')) {
      navigate('?page=login.php', { replace: true });
    }
  }, [location, navigate]);

  const checkCredentials = useCallback((username, password) => {
    // The vulnerable query
    let query = `SELECT * FROM users WHERE username = '${username}' AND password = '${password}'`;
    console.log("Executed query:", query);

    // Check if the password contains a SELECT statement and orders by is_admin
    const lowerPassword = password.toLowerCase();
    return lowerPassword.includes('select') && 
           lowerPassword.includes('password') && 
           lowerPassword.includes('order by') && 
           lowerPassword.includes('is_admin');
  }, []);

  const handleLogin = useCallback((e) => {
    e.preventDefault();
    if (checkCredentials(username, password)) {
      setIsLoggedIn(true);
      navigate('?page=upload.php', { replace: true });
    } else {
      alert('Invalid credentials');
    }
  }, [username, password, checkCredentials, navigate]);

  const handleFileUpload = useCallback((e) => {
    const file = e.target.files[0];
    if (file && (file.name.endsWith('.php2') || file.name.endsWith('.php3'))) {
      setUploadedFile(file);
    } else {
      alert('Invalid file type. Please upload a .php2 or .php3 file.');
    }
  }, []);

  const handleFileSubmission = useCallback(() => {
    if (uploadedFile) {
      setShowFlag(true);
    }
  }, [uploadedFile]);

  const handleThemeChange = (theme) => {
    const newPage = theme === 'dark' ? 'nigol.php' : 'login.php';
    navigate(`?page=${newPage}`, { replace: true });
  };

  const getCurrentPage = () => {
    const searchParams = new URLSearchParams(location.search);
    return searchParams.get('page') || 'login.php';
  };

  const LoginPage = () => (
    <div>
      <Nav variant="tabs" className="mb-3">
        <Nav.Item>
          <Nav.Link active={getCurrentPage() === 'login.php'} onClick={() => handleThemeChange('light')}>Light Theme</Nav.Link>
        </Nav.Item>
      </Nav>
      <Form onSubmit={handleLogin}>
        <Form.Group className="mb-3">
          <Form.Label>Username:</Form.Label>
          <Form.Control 
            type="text" 
            value={username}
            onChange={(e) => setUsername(e.target.value)} // Controlled input
            placeholder="Enter any username"
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Password:</Form.Label>
          <Form.Control 
            type="password" 
            value={password}
            onChange={(e) => setPassword(e.target.value)} // Controlled input
            placeholder="Enter SQL injection"
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Login
        </Button>
      </Form>
    </div>
  );

  const UploadPage = () => (
    <div>
      <div className="mb-3 p-2 bg-light border rounded">
        <strong>Current Page:</strong> {getCurrentPage()}
      </div>
      <h4>File Upload</h4>
      <Form.Group className="mb-3">
        <Form.Label>Upload PHP File (.php2 or .php3 only):</Form.Label>
        <Form.Control 
          type="file" 
          onChange={handleFileUpload}
          accept=".php2,.php3"
        />
      </Form.Group>
      <Button variant="primary" onClick={handleFileSubmission} disabled={!uploadedFile}>
        Submit File
      </Button>
      {showFlag && (
        <Alert variant="success" className="mt-3">
          Congratulations! You've successfully exploited both vulnerabilities. The flag is: {challenge.flag}
        </Alert>
      )}
    </div>
  );

  return (
    <ChallengeInterface challenge={challenge}>
      <Card className="mt-4">
        <Card.Body>
          <Card.Title>{challenge.title}</Card.Title>
          <Card.Text>{challenge.description}</Card.Text>
          <Alert variant="info">
            <strong>Hint:</strong> {challenge.hint}
          </Alert>

          {isLoggedIn ? <UploadPage /> : <LoginPage />}

        </Card.Body>
      </Card>
    </ChallengeInterface>
  );
};

export default WebExploitationChallenge1;
