import React, { useState } from 'react';
import { Card, Form, Button, Alert } from 'react-bootstrap';
import ChallengeInterface from '../components/ChallengeInterface';
import challengeData from '../challengeData';

// Simple SHA1 implementation
function SHA1(msg) {
  function rotate_left(n, s) {
    return (n << s) | (n >>> (32 - s));
  }

  let H0 = 0x67452301, H1 = 0xEFCDAB89, H2 = 0x98BADCFE, H3 = 0x10325476, H4 = 0xC3D2E1F0;
  let W = new Array(80);
  let ml = msg.length * 8;
  let arr = new Array(Math.ceil(ml / 8));

  for (let i = 0; i < arr.length; i++) {
    arr[i] = msg.charCodeAt(i);
  }
  arr[msg.length] = 0x80;

  for (let i = 0; i < arr.length; i++) {
    W[i >> 2] |= arr[i] << (24 - (i % 4) * 8);
  }
  W[((ml + 64 >> 9) << 4) + 15] = ml;

  for (let i = 16; i < 80; i++) {
    W[i] = rotate_left(W[i - 3] ^ W[i - 8] ^ W[i - 14] ^ W[i - 16], 1);
  }

  let a = H0, b = H1, c = H2, d = H3, e = H4;
  for (let i = 0; i < 80; i++) {
    let f, k;
    if (i < 20) { f = (b & c) | ((~b) & d); k = 0x5A827999; }
    else if (i < 40) { f = b ^ c ^ d; k = 0x6ED9EBA1; }
    else if (i < 60) { f = (b & c) | (b & d) | (c & d); k = 0x8F1BBCDC; }
    else { f = b ^ c ^ d; k = 0xCA62C1D6; }

    let temp = (rotate_left(a, 5) + f + e + k + W[i]) & 0xFFFFFFFF;
    e = d;
    d = c;
    c = rotate_left(b, 30);
    b = a;
    a = temp;
  }

  H0 = (H0 + a) & 0xFFFFFFFF;
  H1 = (H1 + b) & 0xFFFFFFFF;
  H2 = (H2 + c) & 0xFFFFFFFF;
  H3 = (H3 + d) & 0xFFFFFFFF;
  H4 = (H4 + e) & 0xFFFFFFFF;

  return (H0.toString(16).padStart(8, '0') +
          H1.toString(16).padStart(8, '0') +
          H2.toString(16).padStart(8, '0') +
          H3.toString(16).padStart(8, '0') +
          H4.toString(16).padStart(8, '0'));
}

const CryptographyChallenge2 = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState('');
  const [serverResponse, setServerResponse] = useState('');
  const [showFlag, setShowFlag] = useState(false);

  const challenge = challengeData.cryptography.find(c => c.id === 'crypto2');

  // Simulated server-side secret
  const serverSecret = "SuperSecretKey123!@#";

  const generateToken = (username, isAdmin) => {
    const adminFlag = isAdmin ? '1' : '0';
    return `${username}:${adminFlag}:${SHA1(username + adminFlag + serverSecret)}`;
  };

  const handleLogin = (e) => {
    e.preventDefault();
    // Simulate server-side login logic
    if (username === 'regularUser' && password === 'password123') {
      const newToken = generateToken(username, false);
      setToken(newToken);
      setServerResponse("Login successful. Token generated.");
    } else {
      setServerResponse("Invalid credentials.");
    }
  };

  const handleAdminAccess = (e) => {
    e.preventDefault();
    // Simulate server-side token verification
    const [tokenUsername, isAdmin, tokenHash] = token.split(':');
    const computedHash = SHA1(tokenUsername + isAdmin + serverSecret);

    if (tokenHash === computedHash) {
      if (isAdmin === '1') {
        setServerResponse("Admin access granted!");
        setShowFlag(true);
      } else {
        setServerResponse("Access denied. User is not an admin.");
      }
    } else {
      setServerResponse("Invalid token.");
    }
  };

  const challengeContent = (
    <Card className="mt-4">
      <Card.Body>
        <Card.Title>{challenge.title}</Card.Title>
        <Card.Text>{challenge.description}</Card.Text>
        <Alert variant="info">
          <strong>Hint:</strong> {challenge.hint}
        </Alert>

        <Form onSubmit={handleLogin}>
          <Form.Group className="mb-3">
            <Form.Label>Username:</Form.Label>
            <Form.Control 
              type="text" 
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter username"
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Password:</Form.Label>
            <Form.Control 
              type="password" 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter password"
            />
          </Form.Group>
          <Button variant="primary" type="submit">
            Login
          </Button>
        </Form>

        {token && (
          <div className="mt-3">
            <h5>Generated Token:</h5>
            <p>{token}</p>
            <Form onSubmit={handleAdminAccess}>
              <Form.Group className="mb-3">
                <Form.Label>Access Token:</Form.Label>
                <Form.Control 
                  type="text" 
                  value={token}
                  onChange={(e) => setToken(e.target.value)}
                  placeholder="Enter or modify your access token"
                />
              </Form.Group>
              <Button variant="success" type="submit">
                Access Admin Panel
              </Button>
            </Form>
          </div>
        )}

        {serverResponse && (
          <Alert variant={serverResponse.includes("granted") ? "success" : "danger"} className="mt-3">
            {serverResponse}
          </Alert>
        )}

        {showFlag && (
          <Alert variant="success" className="mt-3">
            Congratulations! You've successfully bypassed the authentication. The flag is: {challenge.flag}
          </Alert>
        )}
      </Card.Body>
    </Card>
  );

  return (
    <ChallengeInterface challenge={challenge}>
      {challengeContent}
    </ChallengeInterface>
  );
};

export default CryptographyChallenge2;
