import React, { useState, useEffect } from 'react';
import { Card } from 'react-bootstrap';
import { calculateTotalScore } from '../utils/scoreManager';

const ScoreCard = () => {
  const [score, setScore] = useState(0);

  useEffect(() => {
    const updateScore = () => {
      const newScore = calculateTotalScore();
      setScore(newScore);
    };

    updateScore();
    window.addEventListener('storage', updateScore);

    return () => {
      window.removeEventListener('storage', updateScore);
    };
  }, []);

  return (
    <Card 
      className="shadow-sm" 
      style={{ maxWidth: '200px' }}
    >
      <Card.Body className="p-2">
        <div className="d-flex align-items-center">
          <div className="me-3">
            <i className="bi bi-trophy-fill text-warning" style={{ fontSize: '1.5rem' }}></i>
          </div>
          <div>
            <Card.Title className="mb-0 fs-6">Agent Score</Card.Title>
            <Card.Text className="fw-bold mb-0">{score} pts</Card.Text>
          </div>
        </div>
      </Card.Body>
    </Card>
  );
};

export default ScoreCard;
