import React, { useState } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';
import ChallengeInterface from '../ChallengeInterface';

const WebExploitationChallenge1 = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false);
  const TEST = 'REACT_SOURCE_INSPECTOR';

  const handleLogin = (e) => {
    e.preventDefault();
    if (username === 'admin' && password === 'adsjfhasdhfjkdas') {
      setLoggedIn(true);
    } else {
      alert('Incorrect login');
    }
  };

  return (
    <ChallengeInterface challengeId="web-1">
      <div className="text-center">
        <h3>Login Page</h3>
        {/* Hidden flag in a comment */}
        {/* FLAG{REACT_SOURCE_INSPECTOR} */}
        {!loggedIn ? (
          <Form onSubmit={handleLogin}>
            <Form.Group className="mb-3">
              <Form.Label>Username:</Form.Label>
              <Form.Control
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Password:</Form.Label>
              <Form.Control
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
            <Button type="submit" variant="primary">Login</Button>
          </Form>
        ) : (
          <Alert variant="success">
            <h4>Welcome, {username}!</h4>
            <p>Your flag is hidden in the source code.</p>
          </Alert>
        )}
        {/* Hidden flag using a hidden HTML element */}
        <p style={{ display: 'none' }}>
          {`FLAG{${TEST}}`}
        </p>
      </div>
    </ChallengeInterface>
  );
};

export default WebExploitationChallenge1;
