import React, { useState } from 'react';
import { Form, Button, Alert, Card } from 'react-bootstrap';
import challengeData from '../challengeData';
import ChallengeInterface from '../components/ChallengeInterface';

const ReverseEngineeringChallenge1 = () => {
  const [input, setInput] = useState('');
  const [result, setResult] = useState('');

  const challenge = challengeData.reverseEngineering.find(c => c.id === 'rev1');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (input.trim().toUpperCase() === 'CTF{BABY_STEPS_IN_REVERSE}') {
      setResult('success');
    } else {
      setResult('failure');
    }
  };

  const challengeContent = (
    <Card className="mt-4">
      <Card.Body>
        <Card.Title className="text-center mb-4">Phantom's Recruit Test</Card.Title>
        <Alert variant="info" className="mb-3">
          <strong>Hint:</strong> {challenge.hint}
        </Alert>
        <div className="mb-3">
          <Button variant="secondary" href={challenge.files[0]} download>
            Download Challenge Binary
          </Button>
        </div>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3" controlId="formInput">
            <Form.Label>Enter the flag:</Form.Label>
            <Form.Control
              type="text"
              placeholder="CTF{...}"
              value={input}
              onChange={(e) => setInput(e.target.value)}
            />
          </Form.Group>
          <div className="d-grid">
            <Button variant="primary" type="submit">
              Submit Flag
            </Button>
          </div>
        </Form>
        {result === 'success' && (
          <Alert variant="success" className="mt-3">
            <Alert.Heading>Congratulations!</Alert.Heading>
            <p>You've successfully reversed the binary and found the correct flag!</p>
          </Alert>
        )}
        {result === 'failure' && (
          <Alert variant="danger" className="mt-3">
            <Alert.Heading>Incorrect</Alert.Heading>
            <p>That's not the right flag. Keep analyzing the binary!</p>
          </Alert>
        )}
      </Card.Body>
    </Card>
  );

  return challenge ? (
    <ChallengeInterface challenge={challenge}>
      {challengeContent}
    </ChallengeInterface>
  ) : (
    <div>Challenge not found</div>
  );
};

export default ReverseEngineeringChallenge1;
