import React, { useState, useEffect } from 'react';
import { Card, Form, Button, Alert, Nav } from 'react-bootstrap';
import ChallengeInterface from '../components/ChallengeInterface';
import challengeData from '../challengeData';

const WebExploitationChallenge2 = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [userId, setUserId] = useState('1');
  const [pageContent, setPageContent] = useState('');
  const challenge = challengeData.webExploitation.find(c => c.id === 'web2');

  useEffect(() => {
    fetchPageContent();
  }, [currentPage, userId]);

  const fetchPageContent = () => {
    // Simulate API call
    setTimeout(() => {
      if (currentPage === 'profile') {
        if (userId === '999') {  // The "secret" admin ID
          setPageContent("Admin Panel - Congratulations! You've found the admin panel. The flag is: CTF{IDOR_L34DS_T0_PR1V_3SC}");
        } else {
          setPageContent(`User Profile for ID: ${userId}. Nothing interesting here.`);
        }
      } else {
        setPageContent("Welcome to The Phantom Collective's User Portal. Please navigate to view your profile.");
      }
    }, 300);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const handleUserIdChange = (e) => {
    setUserId(e.target.value);
  };

  const challengeContent = (
    <Card className="mt-4">
      <Card.Body>
        <Card.Title className="text-center mb-4">The Phantom's Control Panel</Card.Title>
        <Nav variant="tabs" className="mb-3">
          <Nav.Item>
            <Nav.Link onClick={() => handlePageChange('home')}>Home</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link onClick={() => handlePageChange('profile')}>Profile</Nav.Link>
          </Nav.Item>
        </Nav>
        <Form className="mb-3">
          <Form.Group>
            <Form.Label>User ID:</Form.Label>
            <Form.Control type="text" value={userId} onChange={handleUserIdChange} />
          </Form.Group>
        </Form>
        <Card>
          <Card.Body>
            <h5>{currentPage.charAt(0).toUpperCase() + currentPage.slice(1)}</h5>
            <p>{pageContent}</p>
          </Card.Body>
        </Card>
      </Card.Body>
    </Card>
  );

  return (
    <ChallengeInterface challenge={challenge}>
      {challengeContent}
    </ChallengeInterface>
  );
};

export default WebExploitationChallenge2;
