import React from "react";
import { Container, Row, Col, Button } from "react-bootstrap";
import hackerImage from "../assets/image-hacker.png";
import { useNavigate } from "react-router-dom";


const Home = () => {
  const navigate = useNavigate();

  const handleStartChallenge = () => {
    navigate("/challenge-landing"); // Navigate to the landing page
  };

  const backgroundStyle = {
    background: 'linear-gradient(45deg, #1a1a2e, #16213e, #0f3460)',
    backgroundSize: '400% 400%',
    animation: 'gradient 15s ease infinite',
    height: '100vh',
    overflow: 'hidden',
  };

  const buttonStyle = {
    borderWidth: '2px',
    transition: 'all 0.3s ease',
    ':hover': {
      backgroundColor: 'rgba(255, 255, 255, 0.1)',
      transform: 'translateY(-3px)',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    },
  };
  return (
     <Container fluid style={backgroundStyle}>
      <Row className="justify-content-center align-items-center" style={{ height: '100%' }}>
        <Col xs={12} md={8} lg={6} className="text-center">
          <img 
            src={hackerImage} 
            alt="Hacker" 
            className="img-fluid mb-4" 
            style={{ maxWidth: '250px' }}
          />
          <h1 style={{ color: 'white', marginBottom: '1rem' }}>
            Code Red: Your Digital Espionage Mission Awaits
          </h1>
          <p style={{ color: 'white', marginBottom: '1.5rem' }}>
            Prove your hacking skills in this ultimate cyber challenge!
          </p>
          <Button 
            variant="outline-light" 
            size="lg" 
            onClick={handleStartChallenge}
            style={buttonStyle}
          >
            Initiate Mission
          </Button>
        </Col>
      </Row>
    </Container>
  );
};

export default Home;
